import { useState } from "react";

function Card() {
  const [number1, setNumber1] = useState(0);
  const [number2, setNumber2] = useState(0);
  const [sum, setSum] = useState(0);

function insertNumber1(event){
  const number = (parseFloat(event.target.value));
  setNumber1(number);
}
function insertNumber2(event){
  const number = (parseFloat(event.target.value));
  setNumber2(number);
}
function handleSum(){
  const result = parseFloat(number1) + parseFloat(number2);
  setSum  (result);
}

  return (
    <div>
      <h1>Calculator</h1>
      <input type="number" value= {number1} onChange={insertNumber1}></input><br /><br />
      <input type="number" value= {number2} onChange={insertNumber2}></input><br /><br />
      <button onClick={handleSum}>Somar</button><br /><br />
      {sum !== 0 && (
        <>
        <h1>Resultado da Soma:</h1>
        <span>{ sum }</span>
        </>
      )}
    </div>
  );
}
export default Card;
